    public enum Fonts {
        UBUNTU9X11, SEGOE_SCRIPT11X16, GOST7X13
    }

    public class FontTable {
        private Fonts font;
        private short[] tableArray;
        private int weight;
        private int height;

        public FontTable(Fonts font, short[] tableArray, int weight, int height) {
            this.font = font;
            this.tableArray = tableArray;
            this.weight = weight;
            this.height = height;
        }

        public Fonts getFont() {
            return font;
        }

        public short[] getTableArray() {
            return tableArray;
        }

        public int getWeight() {
            return weight;
        }

        public int getHeight() {
            return height;
        }
    }
